mapper.select:
/* Auth.selectList */
SELECT SEQ seq , AUTH_CODE authCode , AUTH_CODE_NM authCodeNm , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt , RGST_ID rgstId , USE_YN useYn , CTT ctt FROM T_AD_USER_AUTH WHERE USE_YN='Y'
ORDER BY SEQ DESC

mapper.insert:
		/* Auth.insertContents */

			INSERT INTO T_AD_USER_AUTH(
           	   SEQ 
				, AUTH_CODE 
				, AUTH_CODE_NM 
				, RGST_ID 
				, USE_YN 
				, CTT   
			)
			VALUES (
				 (SELECT IFNULL(MAX(SEQ)+1,1) FROM T_AD_USER_AUTH a)
				,#{authCode ,jdbcType=VARCHAR}
				,#{authCodeNm ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
				,#{useYn ,jdbcType=VARCHAR}
				,#{ctt ,jdbcType=VARCHAR}
			)

mapper.update:
/* Auth.updateContents */
	UPDATE T_AD_USER_AUTH SET     
		   AUTH_CODE = #{authCode ,jdbcType=VARCHAR}   
		  , AUTH_CODE_NM = #{authCodeNm ,jdbcType=VARCHAR}   
		  , USE_YN = #{useYn ,jdbcType=VARCHAR}   
		  , CTT = #{ctt ,jdbcType=VARCHAR}   
	      ,RVSE_ID = #{loginSeq ,jdbcType=VARCHAR}   	
	      ,RVSE_DT =  NOW()  	 
	WHERE  SEQ = #{seq}

/* Auth.deleteContents */

	DELETE FROM T_AD_USER_AUTH
	WHERE SEQ = #{seq}

mapper.select:

/* Auth.overlapSelectCount */
	SELECT 
		COUNT(SEQ) cnt 
	FROM T_AD_USER_AUTH
	WHERE AUTH_CODE = #{authCode}

mapper.delete:
/* Auth.authDeleteContents */

	DELETE FROM T_AD_USER_AUTH_MENU
	WHERE ID = #{authCode}

mapper.insert:
		/* Auth.authInsertContents */

			INSERT INTO T_AD_USER_AUTH_MENU(
           	    ID
				,MENU_SEQ
				,RGST_ID
			)
			VALUES (
				#{authCode ,jdbcType=VARCHAR}
				,#{menuSeq ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
			)

mapper.select:
/* Auth.selectList */
SELECT SEQ seq , AUTH_CODE authCode , AUTH_CODE_NM authCodeNm , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt , RGST_ID rgstId , USE_YN useYn , CTT ctt FROM T_AD_USER_AUTH WHERE USE_YN='Y'
AND AUTH_CODE <> '1'
