mapper.select:
/* cmmnBoard.selectList */
SELECT TB.BOARD_SEQ boardSeq , TB.BOARD_GRP_SEQ boardGrpSeq , TB.TITLE title , TB.CONT cont , DATE_FORMAT(TB.RGST_DT,'%Y.%m.%d') rgstDt , TB.RGST_ID rgstId , TB.BOARD_CD boardCd , TB.BOARD_DIVN boardDivn , TB.VIEW_NUM viewNum , ABS(DATEDIFF(TB.RGST_DT,NOW())*24) newChk , TB.SECRET_YN secretYn , (SELECT COUNT(BOARD_SEQ) FROM T_BOARD WHERE USE_YN='Y' AND BOARD_SEQ <> BOARD_GRP_SEQ AND BOARD_GRP_SEQ = TB.BOARD_SEQ) subCnt , F_USER_NM(TB.RGST_ID) name , F_FILE_ID(TB.ATCH_FILE_ID) atchFileId , F_FILE_ID(TB.IMAGE_FILE_ID) imageFileId , F_SITE_NM(REGEXP_REPLACE(SUBSTRING_INDEX(SITE_CLCD,',',1),'[\\[\\]]','') ) siteClcdNm , CHAR_LENGTH(SITE_CLCD) - CHAR_LENGTH(REPLACE(SITE_CLCD,',','')) siteClcdCnt , ACPT_ST_DT acptStDt , ACPT_END_DT acptEndDt , DATE_FORMAT(NOTI_ST_DT,'%Y.%m.%d') notiStDt , DATE_FORMAT(NOTI_END_DT,'%Y.%m.%d') notiEndDt , YEAR year , (SELECT TA.FILE_EXTSN FROM T_ATCH_FILE_DETAIL TA WHERE TA.ATCH_FILE_ID = TB.ATCH_FILE_ID AND TA.DEL_YN='N' LIMIT 1) fileExtsn , CASE WHEN DATE_FORMAT(NOTI_ST_DT,'%Y.%m.%d') <= DATE_FORMAT(NOW(), '%Y.%m.%d') AND DATE_FORMAT(NOW(), '%Y.%m.%d') <= DATE_FORMAT(NOTI_END_DT,'%Y.%m.%d') AND NOTI_YN = 'Y' THEN 'Y' ELSE 'N' END notiYn , TB.STATUS status , TB.QNA_NAME qnaName , TB.QNA_EMAIL qnaEmail , TB.QNA_MBLP qnaMblp FROM T_BOARD TB WHERE USE_YN = 'Y' AND BOARD_SEQ = BOARD_GRP_SEQ AND BOARD_CD = #{boardCd}
ORDER BY notiYn DESC, BOARD_SEQ DESC LIMIT 4
ORDER BY notiYn DESC, BOARD_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
		/* cmmnBoard.insertContents */
			INSERT INTO T_BOARD(
          		BOARD_SEQ 
				, BOARD_GRP_SEQ
				, TITLE
				, CONT
				, ATCH_FILE_ID
				, IMAGE_FILE_ID
				, RGST_ID 
				, NOTI_YN 
				, BOARD_CD 
				, BOARD_DIVN

				, SECRET_YN

				, SITE_CLCD
				, ACPT_ST_DT
				, ACPT_END_DT

					, NOTI_ST_DT


					, NOTI_END_DT

				, YEAR

				, PWD

				, QNA_NAME
				, QNA_EMAIL
				, QNA_MBLP
			)
			VALUES (
				#{boardSeq}
				,#{boardSeq}
				,#{title ,jdbcType=VARCHAR}
				,#{cont ,jdbcType=VARCHAR}
				,#{atchFileId ,jdbcType=VARCHAR}
				,#{imageFileId ,jdbcType=VARCHAR}
				,IFNULL(IF(#{frontLoginSeq ,jdbcType=VARCHAR} ='',null,#{frontLoginSeq ,jdbcType=VARCHAR}),#{loginSeq ,jdbcType=VARCHAR})
				,IFNULL(#{notiYn ,jdbcType=VARCHAR},'N')
				,#{boardCd ,jdbcType=VARCHAR}
				,#{boardDivn ,jdbcType=VARCHAR}

				,#{secretYn, jdbcType=VARCHAR}

				,#{siteClcd, jdbcType=VARCHAR}
				,#{acptStDt, jdbcType=VARCHAR}
				,#{acptEndDt, jdbcType=VARCHAR}

					,#{notiStDt, jdbcType=VARCHAR}


					,#{notiEndDt, jdbcType=VARCHAR}

				,#{year, jdbcType=VARCHAR}

				,#{pwd, jdbcType=VARCHAR}

				,#{qnaName ,jdbcType=VARCHAR}
				,#{qnaEmail ,jdbcType=VARCHAR}
				,#{qnaMblp ,jdbcType=VARCHAR}
			)

		/* cmmnBoard.subInsertContents */

			INSERT INTO T_BOARD(
           	   	  BOARD_SEQ 
				, BOARD_GRP_SEQ
				, TITLE
				, CONT
				, ATCH_FILE_ID 
				, RGST_ID 
				, NOTI_YN 
				, BOARD_CD
				, BOARD_DIVN
				, SITE_CLCD
			)
			VALUES (
				 (SELECT IFNULL(MAX(TB.BOARD_SEQ)+1,1) FROM T_BOARD TB)
				,#{boardGrpSeq ,jdbcType=VARCHAR}
				,CONCAT('RE : ',#{title ,jdbcType=VARCHAR})
				,#{cont ,jdbcType=VARCHAR}
				,#{atchFileId ,jdbcType=VARCHAR}
				,IFNULL(IF(#{frontLoginSeq ,jdbcType=VARCHAR} ='',null,#{frontLoginSeq ,jdbcType=VARCHAR}),#{loginSeq ,jdbcType=VARCHAR})
				,IFNULL(#{notiYn ,jdbcType=VARCHAR},'N')
				,#{boardCd ,jdbcType=VARCHAR}
				,#{boardDivn ,jdbcType=VARCHAR}
				,#{siteClcd, jdbcType=VARCHAR}
			)

mapper.update:
/* cmmnBoard.updateContents */
	UPDATE T_BOARD SET
		 TITLE = #{title ,jdbcType=VARCHAR}
		, CONT = #{cont ,jdbcType=VARCHAR}
		, ATCH_FILE_ID  = #{atchFileId ,jdbcType=VARCHAR}
		, IMAGE_FILE_ID  = #{imageFileId ,jdbcType=VARCHAR}
		, RVSE_DT  = NOW()
		, RVSE_ID  = #{loginSeq ,jdbcType=VARCHAR}
		, NOTI_YN  = IFNULL(#{notiYn ,jdbcType=VARCHAR},'N')
		, SECRET_YN = IFNULL(#{secretYn, jdbcType=VARCHAR},'N')
		, SITE_CLCD = #{siteClcd, jdbcType=VARCHAR}
		, ACPT_ST_DT = #{acptStDt, jdbcType=VARCHAR}
		, ACPT_END_DT = #{acptEndDt, jdbcType=VARCHAR}

			, NOTI_ST_DT = #{notiStDt, jdbcType=VARCHAR}
			, NOTI_END_DT = #{notiEndDt, jdbcType=VARCHAR}

		, YEAR = #{year, jdbcType=VARCHAR}
		, QNA_NAME = #{qnaName, jdbcType=VARCHAR}
		, QNA_MBLP = #{qnaMblp, jdbcType=VARCHAR}
		, QNA_EMAIL = #{qnaEmail, jdbcType=VARCHAR}
	WHERE  BOARD_SEQ = #{boardSeq}

/* cmmnBoard.subUpdateContents */
	UPDATE T_BOARD SET   
		  CONT = #{cont ,jdbcType=VARCHAR}
		, ATCH_FILE_ID  = #{atchFileId ,jdbcType=VARCHAR}
		, SITE_CLCD = #{siteClcd, jdbcType=VARCHAR}
		, RVSE_DT  = NOW()
		, RVSE_ID  = #{loginSeq ,jdbcType=VARCHAR}
	WHERE  BOARD_SEQ = #{boardSeq}

/* cmmnBoard.viewNumUpdateContents */

	UPDATE T_BOARD SET  
		VIEW_NUM = VIEW_NUM + 1
	WHERE  BOARD_SEQ = #{boardSeq}

	/* cmmnBoard.deleteContents */

		UPDATE T_BOARD
	       SET
          	 USE_YN='N'
		WHERE BOARD_SEQ=#{boardSeq}
