mapper.select:

SELECT IFNULL(MAX(FILE_SN),0)+1 AS FILE_SN
FROM T_ATCH_FILE_DETAIL
WHERE ATCH_FILE_ID =  #{atchFileId}

mapper.update:

UPDATE T_ATCH_FILE
SET USE_YN = 'N'
WHERE ATCH_FILE_ID = #{atchFileId}


UPDATE T_ATCH_FILE
SET USE_YN = 'Y'
WHERE ATCH_FILE_ID = #{atchFileId}


UPDATE T_ATCH_FILE_DETAIL
   SET DEL_YN='N'
 WHERE ATCH_FILE_ID = #{atchFileId}
