mapper.select:
/* Mm.selectList */
SELECT PA.* ,(SELECT AUTH_CODE_NM FROM T_AD_USER_AUTH WHERE AUTH_CODE = PA.AUTH_CODE) authCode ,DATE_FORMAT(PA.RGST_DT,'%Y.%m.%d') rgstDt ,DATE_FORMAT(PA.LAST_DATE,'%Y.%m.%d %H:%i:%s') lastDate FROM ( SELECT SEQ seq , ID id , PWD pwd , TEL tel , USE_YN useYn , NAME name , RGST_DT , HP hp , ATCH_FILE_ID atchFileId , EMAIL email , LAST_DATE , AUTH_CODE , EXTNS_NMBR extnsNmbr , SITE_CLCD siteClcd , FAIL_CNT failCnt FROM T_AD_USER A WHERE 1=1 AND USE_YN = 'Y'
) PA ORDER BY PA.seq DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
/* Mm.insertContents */
	INSERT INTO T_AD_USER(
		SEQ
		, ID
		, PWD
		, NAME
	    , AUTH_CODE
		, EMAIL
		, TEL
		, HP
		, DPRT
		, EXTNS_NMBR
		, SITE_CLCD
	) VALUES (
		 #{seq,jdbcType=VARCHAR}
		, #{id ,jdbcType=VARCHAR}
		, #{pwd ,jdbcType=VARCHAR}
		, #{name ,jdbcType=VARCHAR}
		, #{authCode ,jdbcType=VARCHAR}
		, #{email ,jdbcType=VARCHAR}
		, #{tel ,jdbcType=VARCHAR}
		, #{hp ,jdbcType=VARCHAR}
		, #{dprt ,jdbcType=VARCHAR}
		, #{extnsNmbr ,jdbcType=VARCHAR}
		, #{siteClcd ,jdbcType=VARCHAR}
	)

mapper.update:
/* Mm.updateContents */
	UPDATE T_AD_USER SET     
	 	  NAME = #{name ,jdbcType=VARCHAR}	
	      ,RVSE_ID =  #{loginSeq ,jdbcType=VARCHAR}   	
	      ,RVSE_DT =  NOW()  	 

		  ,PWD = #{pwd ,jdbcType=VARCHAR}

		  ,AUTH_CODE =  #{authCode ,jdbcType=VARCHAR}
		  , HP =  #{hp ,jdbcType=VARCHAR}
		  , ATCH_FILE_ID =  #{atchFileId ,jdbcType=VARCHAR}
		  , EMAIL = #{email ,jdbcType=VARCHAR}
		  , USE_YN = IFNULL(#{useYn ,jdbcType=VARCHAR},'Y')
		  , TEL = #{tel ,jdbcType=VARCHAR}
		  , DPRT = #{dprt ,jdbcType=VARCHAR}
		  , EXTNS_NMBR = #{extnsNmbr ,jdbcType=VARCHAR}
		  , SITE_CLCD = #{siteClcd ,jdbcType=VARCHAR}
	WHERE  SEQ = #{seq}

	/* Mm.deleteContents */

		UPDATE T_AD_USER
	       SET
           USE_YN='N'
		WHERE SEQ = #{seq}

mapper.select:
/* Mm.overlapSelectCount */
	SELECT 
		COUNT(SEQ) cnt 
	FROM T_AD_USER
	WHERE ID = #{id}

		AND PWD = #{pwd}

mapper.delete:
/* Mm.authDeleteContents */

	DELETE FROM T_AD_USER_AUTH_MENU
	WHERE ID = #{id}

mapper.insert:
		/* Mm.authInsertContents */

			INSERT INTO T_AD_USER_AUTH_MENU(
           	    ID
				,MENU_SEQ
				,RGST_ID
			)
			VALUES (
				#{id ,jdbcType=VARCHAR}
				,#{menuSeq ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
			)
