mapper.select:
/* Mn.selectList */
SELECT MENU_SEQ menuSeq , MENU_GROUP_SEQ menuGroupSeq , LVL lvl , NO no , MENU_NM menuNm , MENU_CD menuCd , URL url , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt , F_USER_NM(RGST_ID) rgstId , MENU_CL menuCl , DESCRIPTION description , BOARD_CL boardCl FROM T_MENU_INFO WHERE USE_YN='Y' AND MENU_SEQ = MENU_GROUP_SEQ
ORDER BY MENU_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
		/* Mn.insertContents */

			INSERT INTO T_MENU_INFO(
           	   MENU_SEQ
				, MENU_GROUP_SEQ
				, LVL
				, NO
				, MENU_NM
				, MENU_CD
				, URL
				, RGST_ID
				, MENU_CL
				, DESCRIPTION
				, BOARD_CL
			)
			VALUES (
				 (SELECT IFNULL(MAX(TN.MENU_SEQ)+1,1) FROM T_MENU_INFO TN)
				,(SELECT IFNULL(MAX(TN.MENU_SEQ)+1,1) FROM T_MENU_INFO TN)
				,#{lvl ,jdbcType=INTEGER}
				,#{no ,jdbcType=INTEGER}
				,#{menuNm ,jdbcType=VARCHAR}
				,#{menuCd ,jdbcType=VARCHAR}
				,#{url ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
				,#{menuCl ,jdbcType=VARCHAR}
				,#{description ,jdbcType=VARCHAR}
				,#{boardCl ,jdbcType=VARCHAR}
			)

		/* Mn.subInsertContents */

			INSERT INTO T_MENU_INFO(
           	   MENU_SEQ
				, MENU_GROUP_SEQ
				, LVL
				, NO
				, MENU_NM
				, MENU_CD
				, URL
				, RGST_ID
				, MENU_CL
				, DESCRIPTION
				, BOARD_CL
			)
			VALUES (
				 (SELECT IFNULL(MAX(TM.MENU_SEQ)+1,1) FROM T_MENU_INFO TM)
				,#{menuSeq ,jdbcType=INTEGER}
				,#{lvl ,jdbcType=INTEGER}
				,#{no ,jdbcType=INTEGER}
				,#{menuNm ,jdbcType=VARCHAR}
				,#{menuCd ,jdbcType=VARCHAR}
				,#{url ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
				,#{menuCl ,jdbcType=VARCHAR}
				,#{description ,jdbcType=VARCHAR}
				,#{boardCl ,jdbcType=VARCHAR}
			)

mapper.update:
/* Mn.updateContents */

	UPDATE T_MENU_INFO     
	 SET  MENU_NM =#{menuNm ,jdbcType=VARCHAR}
		, MENU_CD =#{menuCd ,jdbcType=VARCHAR}
		, URL 	 = #{url ,jdbcType=VARCHAR}
     	, RVSE_ID =  #{loginSeq ,jdbcType=VARCHAR}   	
      	, RVSE_DT =  NOW()  	 
      	, LVL = #{lvl ,jdbcType=INTEGER}
      	, MENU_CL = #{menuCl ,jdbcType=VARCHAR}
      	, NO = #{no ,jdbcType=INTEGER}
		, DESCRIPTION = #{description ,jdbcType=VARCHAR}
		, BOARD_CL = #{boardCl ,jdbcType=VARCHAR}
	WHERE  MENU_SEQ = #{menuSeq ,jdbcType=INTEGER}

	/* Mn.deleteContents */

		UPDATE T_MENU_INFO
	       SET
          	 USE_YN='N'
		WHERE MENU_SEQ=#{menuSeq}

mapper.select:
/* Mn.menuSelectList */
SELECT MI.MENU_SEQ menuSeq , MI.MENU_GROUP_SEQ menuGroupSeq , MI.LVL lvl , MI.NO no , MI.MENU_NM menuNm , MI.MENU_CD menuCd , MI.URL url , F_USER_NM(MI.RGST_ID) rgstId , DATE_FORMAT(MI.RGST_DT , '%Y.%m.%d') rgstDt , MI.MENU_CL menuCl , CASE WHEN UA.MENU_SEQ IS NULL THEN 'N' ELSE 'Y' END menuChk , MI.DESCRIPTION description , MI.BOARD_CL boardCl , MI.ICON_CL iconCl FROM T_MENU_INFO MI LEFT OUTER JOIN T_AD_USER_AUTH_MENU UA ON MI.MENU_SEQ = UA.MENU_SEQ AND UA.ID = #{authCode} WHERE MI.LVL = #{lvl} AND MI.USE_YN='Y' AND MI.MENU_CL = #{menuCl}
AND MI.MENU_GROUP_SEQ = #{menuSeq}
AND UA.MENU_SEQ IS NOT NULL
ORDER BY MI.NO


/* Mn.overlapSelectCount */
	SELECT 
		COUNT(MENU_SEQ) cnt 
	FROM T_MENU_INFO
	WHERE USE_YN = 'Y'
	  AND MENU_CD = #{menuCd}
	  AND MENU_CL = #{menuCl}


		/* Mn.menuAuthSelectList */
		SELECT
			 MI.MENU_CD menuCd
		FROM T_MENU_INFO MI
            LEFT OUTER JOIN T_AD_USER_AUTH_MENU UA
             ON MI.MENU_SEQ = UA.MENU_SEQ
              AND UA.ID = #{authCode}
		WHERE MI.USE_YN='Y'
			AND MI.LVL = #{lvl}
			AND MI.MENU_CL = #{menuCl}
			AND UA.MENU_SEQ IS NOT NULL

/* Mn.boardDivnSelectContents */
	SELECT
		BOARD_CL boardCl
	FROM T_MENU_INFO
	WHERE  USE_YN='Y'
		AND MENU_CL = #{schEtc09}
		AND MENU_CD = #{boardCd}

/* Mn.menuNmSelectContents */
	SELECT
		MENU_NM menuNm
	FROM T_MENU_INFO
	WHERE  USE_YN='Y'
		AND MENU_CD = #{menuCd}
		AND MENU_CL = #{menuCl}

/* Mn.menuSelectList */
SELECT MI.MENU_SEQ menuSeq , MI.MENU_GROUP_SEQ menuGroupSeq , MI.LVL lvl , MI.NO no , MI.MENU_NM menuNm , MI.MENU_CD menuCd , MI.URL url , F_USER_NM(MI.RGST_ID) rgstId , DATE_FORMAT(MI.RGST_DT , '%Y.%m.%d') rgstDt , MI.MENU_CL menuCl , MI.DESCRIPTION description , MI.BOARD_CL boardCl , MI.ICON_CL iconCl FROM T_MENU_INFO MI WHERE MI.LVL = #{lvl} AND MI.USE_YN='Y' AND MI.MENU_CL = #{menuCl}
AND MI.MENU_GROUP_SEQ = #{menuSeq}
ORDER BY MI.NO

/* mn.menuSelectList */ SELECT MENU_SEQ menuSeq , MENU_GROUP_SEQ menuGroupSeq , MENU_CD menuCd , MENU_NM menuNm FROM T_MENU_INFO WHERE USE_YN = 'Y' AND MENU_CL = 'ft' AND BOARD_CL='7' AND LVL = #{lvl}
AND MENU_GROUP_SEQ = (SELECT MENU_SEQ FROM T_MENU_INFO WHERE MENU_CD = #{menuCd} AND MENU_CL ='ft' AND USE_YN='Y')
ORDER BY NO
