mapper.select:
/* Sample.selectList */
SELECT SEQ seq , TITLE title , CONT cont , DATE_FORMAT(RGST_DT,'%Y.%m.%d') rgstDt , RGST_ID rgstId , F_USER_NM(RGST_ID) name , ATCH_FILE_ID atchFileId , STA_DATE staDate , END_DATE endDate , HP hp , MAIL mail FROM T_SAMPLE TS WHERE USE_YN='Y'
ORDER BY SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
		/* Sample.insertContents */

			INSERT INTO T_SAMPLE(
           	  	  SEQ 
				, TITLE 
				, CONT
				, RGST_ID 
				, ATCH_FILE_ID 
				, STA_DATE
				, END_DATE 
				, NOTI_YN 
				, HP
				, MAIL
			)
			VALUES (
				 (SELECT IFNULL(MAX(TS.SEQ)+1,1) FROM T_SAMPLE TS)
				,#{title ,jdbcType=VARCHAR}
				,#{cont ,jdbcType=VARCHAR}
				,#{loginSeq ,jdbcType=VARCHAR}
				,#{atchFileId ,jdbcType=VARCHAR}
				,#{staDate ,jdbcType=VARCHAR}
				,#{endDate ,jdbcType=VARCHAR}
				,#{notiYn ,jdbcType=VARCHAR}
				,#{hp ,jdbcType=VARCHAR}
				,#{mail ,jdbcType=VARCHAR}
			)

mapper.update:
/* Sample.updateContents */

	UPDATE T_SAMPLE     
	 SET 
		  TITLE = #{title ,jdbcType=VARCHAR} 
		, CONT = #{cont ,jdbcType=VARCHAR}
		, RVSE_DT = NOW()
		, ATCH_FILE_ID = #{atchFileId ,jdbcType=VARCHAR}
		, STA_DATE = #{staDate ,jdbcType=VARCHAR}
		, END_DATE  = #{endDate ,jdbcType=VARCHAR}
		, NOTI_YN  = #{notiYn ,jdbcType=VARCHAR}
		, HP = #{hp ,jdbcType=VARCHAR}
		, MAIL = #{mail ,jdbcType=VARCHAR}
	WHERE  SEQ = #{seq}

	/* Sample.deleteContents */

		UPDATE T_SAMPLE
	       SET
          	 USE_YN='N'
		WHERE SEQ=#{seq}
